document.addEventListener('DOMContentLoaded',function(){const buttons=document.querySelectorAll('.btn');buttons.forEach(btn=>{btn.addEventListener('click',function(e){let x=e.clientX-e.target.offsetLeft;let y=e.clientY-e.target.offsetTop;let ripple=document.createElement('span');ripple.style.left=x+'px';ripple.style.top=y+'px';ripple.classList.add('ripple');this.appendChild(ripple);setTimeout(()=>{ripple.remove();},600);});});window.onload=function(){document.querySelector("form").reset();};});